    request:
      method: POST
      endpoint: /resources
      headers:
        Authorization: Bearer {token}
        Content-Type: application/json
      body: |
        {
          "name": "Example Resource",
          "description": "Resource description"
        }

    response:
      status: 201
      headers:
        Location: /resources/123
      body: |
        {
          "id": 123,
          "name": "Example Resource",
          "created_at": "2023-01-01T00:00:00Z"
        }
